import { useState, useEffect } from 'react';

import { Box, Typography, styled } from '@mui/material';




const Header = styled(Box)`
    padding: 15px 24px;
    background: #fff;
    borderBottom: 1px solid #f0f0f0;
`;

const Heading = styled(Typography)`
    color: #878787;
`;

const Container = styled(Box)`
    padding: 15px 24px;
    background: #fff;
    & > p {
        margin-bottom: 20px;
        font-size: 14px;
    }
`;

const Price = styled(Typography)`
    float: right;
`;



const TotalAmount = styled(Typography)`
    font-size: 18px;
    font-weight: 600;
    border-top: 1px dashed #e0e0e0;
    padding: 20px 0;
    border-bottom: 1px dashed #e0e0e0;
`;

const Discount = styled(Typography)`
    font-size: 16px; 
    color: green;
`

// component: {
//     // width: '30%'
// },


const TotalView = ({ cartItems }) => {
    // Calculate total prices
    const totalPrice = cartItems.reduce((acc, item) => acc + (item.price.cost * item.quantity), 0);
    const totalMRP = cartItems.reduce((acc, item) => acc + (item.price.mrp * item.quantity), 0);
    const totalDiscount = totalMRP - totalPrice;

    return (
        <Container>
            <Header>PRICE DETAILS</Header>
            <Box>
                <Typography>Price ({cartItems.length} items): ₹{totalMRP}</Typography>
                <Discount>Discount: ₹{totalDiscount}</Discount>
                <TotalPrice>Total Amount: ₹{totalPrice}</TotalPrice>
                <Typography style={{ color: 'green' }}>You will save ₹{totalDiscount} on this order</Typography>
            </Box>
        </Container>
    );
};

export default TotalView;
